import React from "react";

const ForecastCard = ({ day }) => {
  const date = new Date(day.date);

  const dayOfWeek = date.toLocaleDateString("en-US", { weekday: "long" }); // Correct: Use "long" for full weekday name (e.g., Monday)

  // Corrected: "MMM" -> "short"
  const formattedDate = date.toLocaleDateString("en-US", {
    month: "short", // Corrected: Use "short" for abbreviated month (e.g., Jan)
    day: "2-digit", // Correct: Use "2-digit" for two-digit day (e.g., 01)
    year: "numeric", // Correct: Use "numeric" for full year (e.g., 2023)
  });

  return (
    <div className="card rounded-3 mb-3">
      <div className="card-body text-center">
        <h5 className="card-title">{dayOfWeek}</h5>
        <p className="card-text">{formattedDate}</p>
        <img
          src={day.day.condition.icon}
          alt={day.day.condition.text}
          className="mb-2"
        />
        <p className="card-text">
          High: {day.day.maxtemp_f}°F / Low: {day.day.mintemp_f}°F
        </p>
        <p className="card-text">
          {day.day.daily_chance_of_rain > 0
            ? `Rain: ${day.day.daily_chance_of_rain}%`
            : ""}
          {day.day.daily_chance_of_snow > 0
            ? `Snow: ${day.day.daily_chance_of_snow}%`
            : ""}
        </p>
      </div>
    </div>
  );
};

export default ForecastCard;
