import React, { useState, useEffect } from "react";
import SearchBar from "./components/SearchBar";
import CurrentWeather from "./components/CurrentWeather";
import DailyForecast from "./components/DailyForecast";
import HourlyForecast from "./components/HourlyForecast";
import { getWeatherData, getCurrentWeatherData } from "./api";
import "./App.css";

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [currentWeatherData, setCurrentWeatherData] = useState(null);
  const [location, setLocation] = useState("New York");
  const [isLoading, setIsLoading] = useState(true); // Add loading state
  const [error, setError] = useState(null); // Add error state

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true); // Start loading
      setError(null); // Clear any previous errors
      try {
        const data = await getWeatherData(location);
        setWeatherData(data);
      } catch (error) {
        console.error("Error in App.js:", error);
        setError("Failed to load weather data."); // Set error message
        setWeatherData(null);
      } finally {
        setIsLoading(false); // Stop loading
      }
    };
    fetchData();
  }, [location]);

  useEffect(() => {
    const fetchCurrentData = async () => {
      try {
        const data = await getCurrentWeatherData(location);
        setCurrentWeatherData(data);
      } catch (error) {
        console.error("Error in App.js:", error);
        setCurrentWeatherData(null);
      }
    };
    fetchCurrentData();
  }, [location]);

  const handleSearch = (newLocation) => {
    setLocation(newLocation);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Weather App</h1>
      <SearchBar onSearch={handleSearch} />
      {isLoading && <div className="text-center">Loading...</div>}{" "}
      {/* Display loading message */}
      {error && <div className="text-center text-danger">{error}</div>}{" "}
      {/* Display error message */}
      {!isLoading && currentWeatherData && (
        <CurrentWeather data={currentWeatherData} />
      )}
      {!isLoading && weatherData && (
        <DailyForecast forecast={weatherData.forecast} />
      )}
      {!isLoading && weatherData && (
        <HourlyForecast forecast={weatherData.forecast} />
      )}
    </div>
  );
}

export default App;
