import React from "react";

const CurrentWeather = ({ data }) => {
  if (!data) return null;

  const { current, location } = data;
  const date = new Date(current.last_updated);

  // Corrected: "MMM" -> "short"
  const formattedDate = date.toLocaleDateString("en-US", {
    month: "short", // Corrected: Use "short" for abbreviated month (e.g., Jan)
    day: "2-digit", // Correct: Use "2-digit" for two-digit day (e.g., 01)
    year: "numeric", // Correct: Use "numeric" for full year (e.g., 2023)
  });

  const dayOfWeek = date.toLocaleDateString("en-US", { weekday: "long" }); // Correct: Use "long" for full weekday name (e.g., Monday)
  const formattedTime = date.toLocaleTimeString("en-US", {
    hour: "numeric", // Correct: Use "numeric" for hour (e.g., 3)
    minute: "numeric", // Correct: Use "numeric" for minute (e.g., 45)
    hour12: true, // Correct: Use 12-hour format (e.g., 3:45 PM)
  });

  return (
    <div className="card mb-4">
      <div className="card-body">
        <h2 className="card-title">
          {location.name}, {location.region}, {location.country}
        </h2>
        <p className="card-text">
          {formattedDate}, {dayOfWeek} {formattedTime}
        </p>
        <div className="d-flex align-items-center">
          <img
            src={current.condition.icon}
            alt={current.condition.text}
            className="me-3"
          />
          <div>
            <h3 className="mb-0">{current.temp_f}°F</h3>
            <p className="mb-0">{current.condition.text}</p>
          </div>
        </div>
        <p className="card-text">Feels Like: {current.feelslike_f}°F</p>
        <p className="card-text">
          Wind: {current.wind_mph} mph {current.wind_dir}
        </p>
        <p className="card-text">Humidity: {current.humidity}%</p>
      </div>
    </div>
  );
};

export default CurrentWeather;
